	void GameMap::LoadBitmap() {
		blue.LoadBitmap(IDB_BLUE, RGB(181, 230, 29));
		green.LoadBitmap(IDB_GREEN, RGB(181, 230, 29));
		block.LoadBitmap(IDB_BLOCK, RGB(181, 230, 29));
		block2.LoadBitmap(IDB_BLOCK2, RGB(181, 230, 29));
		floor.LoadBitmap(IDB_FLOOR, RGB(181, 230, 29));
		floor_D.LoadBitmap(IDB_FLOOR_D, RGB(181, 230, 29));


	}
	int GameMap::IsEmpty(int x, int y) {
		int gx = x / MH;
		int gy = y / MW;
		if ((map[gy][gx] == 0))
			return 1;
		else if ((map[gy][gx] == 4))
			return 4;
		else if ((map[gy][gx] == 5))
			return 5;
		else
			return 0;
	}  
	void GameMap::SetMapXY(int nx, int ny) {
		X = nx;
		Y = ny;
	}
	void GameMap::setLevel() {
		level++;
	}
	int GameMap::getLevel() {
		return level;
	}

	void GameMap::OnShow() {
		for (int i = 0; i < 120; i++)
		{
			for (int j = 0; j < 40; j++)
			{
				int sx = i * MW - X;
				int sy = j * MH - Y;

				switch (map[j][i])
				{
				case 0:
					break;
				case 1:
					block.SetTopLeft(sx,sy);
					block.ShowBitmap();
					break;
				case 2:
					floor.SetTopLeft(sx, sy);
					floor.ShowBitmap();
					break;
				case 3:
					floor_D.SetTopLeft(sx, sy);
					floor_D.ShowBitmap();
					break;
				case 4:
					break;
				case 5:
					break;
				case 6:
					break;
				default:
					ASSERT(0);

				}
			}

		}
	}
	int GameMap::ScreenX(int x) {
		return x - X;
	}
	int GameMap::ScreenY(int y) {
		return y - Y;
	}
	void GameMap::OnMove() {
		if (isMovingLeft)
			X -= 20;
		if (isMovingRight)
			X += 20;
		if (isMovingUp)
			Y -= 10;
		if (isMovingDown)
			Y += 20;
	}
	void GameMap::SetMovingDown(bool flag)
	{
		isMovingDown = flag;
	}

	void GameMap::SetMovingLeft(bool flag)
	{
		isMovingLeft = flag;
	}

	void GameMap::SetMovingRight(bool flag)
	{
		isMovingRight = flag;
	}

	void GameMap::SetMovingUp(bool flag)
	{
		isMovingUp = flag;
	}
